CREATE TABLE IF NOT EXISTS `berli_listview_colors` (
				  `listfieldid` int(19) DEFAULT NULL,
				  `listcolor` varchar(7) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT '#FFFFFF',
				  `fieldcontent` varchar(255) NOT NULL
				) ENGINE=InnoDB DEFAULT CHARSET=utf8  COLLATE=utf8_unicode_ci;
